from unittest import TestCase
from euler_main import take_decimal_string


class EulerTest(TestCase):

    def test_simple(self):
        self.assertEqual(take_decimal_string("0.1"), 1)

    def test_with_ten_dec(self):
        self.assertEqual(take_decimal_string("0.1000000002"), 2)
        
    def test_with_hundred_dec(self):
        self.assertEqual(take_decimal_string("0.1000000002100000000310000000031000000003100000000310000000031000000003100000000310000000031000000003"), 6)
    
    def test_with_thousand_dec(self):
        self.assertEqual(take_decimal_string("0."), 24)
